@import url("https://fonts.googleapis.com/css2?family=Overpass:wght@300;600&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Ubuntu:wght@400;500;700&display=swap");

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

//primary
$light-red-color: hsl(356, 100%, 66%);
$text-red-color: #ff505c;
$lighter-red-color: hsl(355, 100%, 74%);
$dark-blue-color: hsl(208, 49%, 24%);

//neutral
$white-color: hsl(0, 0%, 100%);
$grayish-blue-color: hsl(240, 2%, 79%);
$darkest-blue-color: hsl(240, 10%, 16%);

//background gradient
$light-red-color: hsl(13, 100%, 72%);
$red-color: hsl(353, 100%, 62%);

//fonts
$overpass-font: "Overpass", sans-serif;
$ubuntu-font: "Ubuntu", sans-serif;

@mixin button($background, $color) {
  background: $background;
  color: $color;
  line-height: 18px;
  font-weight: bold;
  font-family: $ubuntu-font;
  border-radius: 28px;
}

.attribution {
  font-size: 11px;
  text-align: center;
  a {
    color: hsl(228, 45%, 44%);
  }
}

ul {
  list-style: none;
}

a {
  text-decoration: none;
}

button {
  background: transparent;
  border: none;
  cursor: pointer;
  outline: none;
  color: $white-color;
}

.container {
  background-color: #fafafa;
}

